-- AI API Keys table for BYOK (Bring Your Own Key) module
-- Stores user's API keys for Anthropic (Claude) and Perplexity
-- Phase 1: Plain text with RLS only (no encryption)
-- Upgrade path: Add encryption when porting to production multi-user apps

CREATE TABLE user_ai_keys (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  anthropic_key TEXT,
  perplexity_key TEXT,
  keys_verified BOOLEAN DEFAULT false,
  last_verified_at TIMESTAMP WITH TIME ZONE,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  UNIQUE(user_id)
);

-- Create index for user lookups
CREATE INDEX idx_user_ai_keys_user_id ON user_ai_keys(user_id);

-- Enable RLS to isolate user data
ALTER TABLE user_ai_keys ENABLE ROW LEVEL SECURITY;

-- RLS Policies
CREATE POLICY "Users can view own keys"
  ON user_ai_keys FOR SELECT
  USING (auth.uid() = user_id);

CREATE POLICY "Users can insert own keys"
  ON user_ai_keys FOR INSERT
  WITH CHECK (auth.uid() = user_id);

CREATE POLICY "Users can update own keys"
  ON user_ai_keys FOR UPDATE
  USING (auth.uid() = user_id);

CREATE POLICY "Users can delete own keys"
  ON user_ai_keys FOR DELETE
  USING (auth.uid() = user_id);

-- Add comments for documentation
COMMENT ON TABLE user_ai_keys IS 'BYOK module: Stores user API keys for AI features (Claude, Perplexity)';
COMMENT ON COLUMN user_ai_keys.anthropic_key IS 'Anthropic (Claude) API key starting with sk-ant-api03-';
COMMENT ON COLUMN user_ai_keys.perplexity_key IS 'Perplexity AI API key starting with pplx-';
