codeExamples.errorBoundary:
// components/markdown/markdown-editor-boundary.tsx
import React, { Component, ReactNode } from 'react';
import { toast } from 'sonner-native';

interface Props {
  children: ReactNode;
  fallback: ReactNode; // Old editor component
}

interface State {
  hasError: boolean;
}

export class MarkdownEditorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError() {
    return { hasError: true };
  }

  componentDidCatch(error: Error) {
    console.error('Markdown editor crashed:', error);
    toast.error('Editor error - switched to legacy mode');
  }

  render() {
    if (this.state.hasError) {
      return this.props.fallback;
    }
    return this.props.children;
  }
}

// Usage:
// <MarkdownEditorBoundary fallback={<LegacyNoteModal />}>
//   <MarkdownEditor />
// </MarkdownEditorBoundary>

