// lib/feature-flags.ts
export const FeatureFlags = {
  USE_MARKDOWN_EDITOR: true, // Toggle to switch between old and new editor
};

// In app/(tabs)/index.tsx:
import { FeatureFlags } from '@/lib/feature-flags';
import { MarkdownEditorBoundary } from '@/components/markdown/markdown-editor-boundary';

function handleNewNote() {
  if (FeatureFlags.USE_MARKDOWN_EDITOR) {
    router.push('/note/new'); // New markdown editor
  } else {
    setShowModal(true); // Old modal
  }
}

// Wrap new editor with boundary:
if (FeatureFlags.USE_MARKDOWN_EDITOR) {
  return (
    <MarkdownEditorBoundary fallback={<NoteModal />}>
      <MarkdownEditor />
    </MarkdownEditorBoundary>
  );
}
