interface MarkdownEditorProps {
  // ... existing props
  onUndoRedoChange?: (canUndo: boolean, canRedo: boolean) => void;
}

// Inside component, notify parent of undo/redo availability
useEffect(() => {
  onUndoRedoChange?.(canUndo, canRedo);
}, [canUndo, canRedo, onUndoRedoChange]);

// Alternative: Use imperative handle with forwardRef
useImperativeHandle(ref, () => ({
  undo: handleUndo,
  redo: handleRedo,
  canUndo,
  canRedo
}), [handleUndo, handleRedo, canUndo, canRedo]);

// In parent component:
const editorRef = useRef<{undo: () => void, redo: () => void, canUndo: boolean, canRedo: boolean}>(null);
const [canUndo, setCanUndo] = useState(false);
const [canRedo, setCanRedo] = useState(false);

// Using callback approach:
<MarkdownEditor
  onUndoRedoChange={(canUndo, canRedo) => {
    setCanUndo(canUndo);
    setCanRedo(canRedo);
  }}
/>

// OR using ref approach:
<MarkdownEditor ref={editorRef} />

