// supabase/functions/publish-note/index.ts
// PRODUCTION-READY VERSION - All security and logic bugs fixed
// v2.5: CORS on ALL responses + published_at preservation + rate limit AFTER publish
import { serve } from 'https://deno.land/std@0.168.0/http/server.ts'
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2'

// CORS headers for web app compatibility
const corsHeaders = {
  'Content-Type': 'application/json',
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

serve(async (req) => {
  // Request method validation
  if (req.method === 'OPTIONS') {
    return new Response('ok', { status: 200, headers: corsHeaders });
  }
  if (req.method !== 'POST') {
    return new Response('Method not allowed', { status: 405, headers: { ...corsHeaders, 'Allow': 'POST' } });
  }

  try {
    // 1. Get JWT
    const authHeader = req.headers.get('Authorization');
    if (!authHeader) {
      console.log('âŒ No Authorization header');
      return new Response(JSON.stringify({ success: false, error: 'UNAUTHORIZED' }), { status: 401, headers: corsHeaders });
    }

    // 2. Create service_role client (bypasses RLS - only for Edge Functions)
    const supabase = createClient(Deno.env.get('SUPABASE_URL')!, Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!);

    // 3. Verify user
    const { data: { user }, error: authError } = await supabase.auth.getUser(authHeader.replace('Bearer ', ''));
    if (authError || !user) {
      console.log('âŒ Auth verification failed:', authError);
      return new Response(JSON.stringify({ success: false, error: 'UNAUTHORIZED' }), { status: 401, headers: corsHeaders });
    }
    console.log(`âœ… User authenticated: ${user.id}`);

    // 4. Parse body
    const { noteId, noteTitle, noteContent } = await req.json();
    console.log(`ðŸ“ Publishing note ${noteId}`);

    // 5. SECURITY: Validate note ownership
    const { data: note, error: noteError } = await supabase.from('notes').select('user_id').eq('id', noteId).single();
    if (noteError || !note) {
      console.log('âŒ Note not found:', noteId);
      return new Response(JSON.stringify({ success: false, error: 'NOTE_NOT_FOUND' }), { status: 404, headers: corsHeaders });
    }
    if (note.user_id !== user.id) {
      console.log(`âŒ Unauthorized: User ${user.id} tried to publish note ${noteId} owned by ${note.user_id}`);
      return new Response(JSON.stringify({ success: false, error: 'UNAUTHORIZED' }), { status: 401, headers: corsHeaders });
    }
    console.log('âœ… Note ownership verified');

    // 6. Validate inputs
    if (!noteContent || noteContent.trim().length === 0) {
      return new Response(JSON.stringify({ success: false, error: 'NOTE_EMPTY' }), { status: 400, headers: corsHeaders });
    }
    if (noteContent.length > 100000) {
      return new Response(JSON.stringify({ success: false, error: 'NOTE_TOO_LARGE' }), { status: 400, headers: corsHeaders });
    }

    // 7. CRITICAL FIX: Check rate limit (DON'T increment yet)
    const { data: rateLimit } = await supabase.from('publish_rate_limits').select('*').eq('user_id', user.id).maybeSingle();
    const now = new Date();
    const nextMidnight = new Date();
    nextMidnight.setUTCHours(24, 0, 0, 0);

    let currentCount = 0;
    let needsReset = false;

    if (rateLimit) {
      const resetAt = new Date(rateLimit.reset_at);
      needsReset = now >= resetAt;
      if (!needsReset) {
        currentCount = rateLimit.publish_count_today;
        if (currentCount >= 50) {
          console.log(`âŒ Rate limit exceeded: ${currentCount}/50`);
          return new Response(JSON.stringify({ success: false, error: 'RATE_LIMIT_EXCEEDED' }), { status: 429, headers: corsHeaders });
        }
      }
    }
    console.log(`ðŸ“Š Current rate limit: ${currentCount}/50 (needsReset: ${needsReset})`);

    // 8. Generate slug
    const effectiveTitle = (noteTitle && noteTitle.trim() && noteTitle.toLowerCase() !== 'untitled')
      ? noteTitle : noteContent.split('\n').find(line => line.trim().length > 0)?.substring(0, 60) || 'untitled-note';
    let baseSlug = generateSlug(effectiveTitle);
    console.log(`ðŸ”— Generated base slug: ${baseSlug}`);

    // 9. 5-retry slug collision logic
    let finalSlug = baseSlug;
    let attempts = 0;
    const MAX_ATTEMPTS = 5;
    while (attempts < MAX_ATTEMPTS) {
      const { data: existingSlug } = await supabase.from('published_notes').select('slug, note_id').eq('slug', finalSlug).maybeSingle();
      if (!existingSlug || existingSlug.note_id === noteId) {
        console.log(`âœ… Slug available: ${finalSlug}`);
        break;
      }
      finalSlug = `${baseSlug}-${randomSuffix()}`;
      attempts++;
      console.log(`âš ï¸ Slug collision (attempt ${attempts}/${MAX_ATTEMPTS})`);
    }
    if (attempts === MAX_ATTEMPTS) {
      finalSlug = `note-${crypto.randomUUID().substring(0, 8)}`;
      console.log(`ðŸ†” UUID fallback: ${finalSlug}`);
    }

    // 10. CRITICAL FIX: Check if already published (explicit INSERT/UPDATE)
    const { data: existingPublish } = await supabase.from('published_notes').select('id, published_at').eq('note_id', noteId).maybeSingle();

    if (existingPublish) {
      // Republish - UPDATE only slug (preserve original published_at)
      const { error: updateError } = await supabase.from('published_notes').update({ slug: finalSlug }).eq('note_id', noteId);
      if (updateError) {
        console.error('âŒ Database update error:', updateError);
        return new Response(JSON.stringify({ success: false, error: 'DATABASE_ERROR' }), { status: 500, headers: corsHeaders });
      }
      console.log(`âœ… Note republished (preserved published_at: ${existingPublish.published_at})`);
    } else {
      // First publish - INSERT with new published_at
      const { error: insertError } = await supabase.from('published_notes').insert({
        note_id: noteId,
        user_id: user.id,
        slug: finalSlug,
        published_at: now.toISOString()
      });
      if (insertError) {
        console.error('âŒ Database insert error:', insertError);
        return new Response(JSON.stringify({ success: false, error: 'DATABASE_ERROR' }), { status: 500, headers: corsHeaders });
      }
      console.log(`âœ… Note published for first time`);
    }

    // 11. CRITICAL FIX: NOW increment rate limit (AFTER successful publish)
    if (!rateLimit) {
      console.log('ðŸ“Š Creating rate limit row (first publish)');
      await supabase.from('publish_rate_limits').insert({
        user_id: user.id,
        publish_count_today: 1,
        last_publish_at: now.toISOString(),
        reset_at: nextMidnight.toISOString()
      });
    } else if (needsReset) {
      console.log('ðŸ”„ Resetting rate limit counter (new day)');
      await supabase.from('publish_rate_limits').update({
        publish_count_today: 1,
        last_publish_at: now.toISOString(),
        reset_at: nextMidnight.toISOString()
      }).eq('user_id', user.id);
    } else {
      await supabase.from('publish_rate_limits').update({
        publish_count_today: currentCount + 1,
        last_publish_at: now.toISOString()
      }).eq('user_id', user.id);
      console.log(`ðŸ“Š Rate limit: ${currentCount + 1}/50`);
    }

    // 12. Return success
    const baseUrl = Deno.env.get('BASE_URL');
    if (!baseUrl) {
      console.error('âŒ BASE_URL environment variable not set!');
      return new Response(JSON.stringify({ success: false, error: 'DATABASE_ERROR' }), { status: 500, headers: corsHeaders });
    }
    const publicUrl = `${baseUrl}/p/${finalSlug}`;
    console.log(`âœ… Note published: ${publicUrl}`);
    return new Response(JSON.stringify({ success: true, slug: finalSlug, publicUrl }), { status: 200, headers: corsHeaders });

  } catch (error) {
    console.error('âŒ Unexpected error:', error);
    return new Response(JSON.stringify({ success: false, error: 'DATABASE_ERROR' }), { status: 500, headers: corsHeaders });
  }
});

function generateSlug(title: string): string {
  const slug = title.toLowerCase().replace(/[^a-z0-9\s-]/g, '').replace(/\s+/g, '-').replace(/-+/g, '-').replace(/^-+|-+$/g, '').substring(0, 60);
  if (/^\d/.test(slug)) return `note-${slug}`;
  return slug || 'untitled-note';
}

function randomSuffix(): string {
  return Math.random().toString(36).substring(2, 8);
}

