// services/publish/publish-service.ts (UPDATED - FIXED ERROR MAPPING)
import { supabase } from '@/lib/supabase';
import type { PublishResult, PublishError } from './types';

export async function publishNote(noteId: string, noteTitle: string, noteContent: string): Promise<PublishResult> {
  try {
    const { data, error } = await supabase.functions.invoke('publish-note', {
      body: { noteId, noteTitle, noteContent }
    });

    if (error) {
      console.error('Edge Function error:', error);

      // Map HTTP status codes to error types
      const status = error.context?.status || error.status;

      if (status === 400) {
        // Validation errors (NOTE_EMPTY, NOTE_TOO_LARGE)
        // Edge Function returns { success: false, error: 'NOTE_EMPTY' } in body
        // But Supabase client treats 400 as error, so we need to parse it
        try {
          const errorBody = typeof error === 'string' ? JSON.parse(error) : error;
          return { success: false, error: errorBody.error || 'NOTE_EMPTY' };
        } catch {
          return { success: false, error: 'NOTE_EMPTY' };
        }
      }

      if (status === 401) {
        return { success: false, error: 'UNAUTHORIZED' };
      }

      if (status === 404) {
        return { success: false, error: 'NOTE_NOT_FOUND' };
      }

      if (status === 429) {
        return { success: false, error: 'RATE_LIMIT_EXCEEDED' };
      }

      if (status === 500 || status === 503) {
        return { success: false, error: 'DATABASE_ERROR' };
      }

      // Unknown error
      return { success: false, error: 'DATABASE_ERROR' };
    }

    // Success: data contains { success: true, slug, publicUrl }
    return data as PublishResult;

  } catch (error) {
    console.error('Network error:', error);
    return { success: false, error: 'NETWORK_ERROR' };
  }
}

