import { Alert } from 'react-native';

const handleUnpublish = () => {
  Alert.alert(
    'Unpublish note?',
    'The public link will stop working. You can publish again later.',
    [
      { text: 'Cancel', style: 'cancel' },
      {
        text: 'Unpublish',
        style: 'destructive',
        onPress: async () => {
          const loadingToast = toast.loading('Unpublishing note...');
          const result = await unpublishNote(noteId);
          if (result.success) {
            toast.success('Note unpublished', { id: loadingToast });
            onPublishChanged();
          } else {
            toast.error(ERROR_MESSAGES[result.error!], { id: loadingToast });
          }
        }
      }
    ]
  );
};

