// services/markdown-service.ts
import { marked } from 'marked';
import Markdown from 'react-native-markdown-display';

export const markdownService = {
  renderToComponents(markdown: string): JSX.Element {
    return <Markdown>{markdown}</Markdown>;
  },

  renderToHtml(markdown: string): string {
    return marked(markdown);
  },

  renderToDocument(title: string, markdown: string): string {
    return `<!DOCTYPE html>\n<html>\n<head>\n<title>${title}</title>\n<style>body { font-family: sans-serif; max-width: 800px; margin: 40px auto; }</style>\n</head>\n<body>\n${marked(markdown)}\n</body>\n</html>`;
  }
};

codeExamples.noteParser:
// utils/note-parser.ts
export function extractTitle(markdown: string): string {
  const lines = markdown.trim().split('\n');
  const firstLine = lines[0]?.trim() || '';

  if (firstLine.startsWith('#')) {
    return firstLine.replace(/^#+\s*/, ''); // Remove # symbols
  }

  if (firstLine) {
    return firstLine.substring(0, 500); // Limit to VARCHAR(500)
  }

  return `Note ${new Date().toLocaleString()}`; // Fallback
}

export function parseNote(markdown: string) {
  return {
    title: extractTitle(markdown),
    content: markdown
  };
}

export function getPreviewText(markdown: string, maxLength = 100): string {
  const lines = markdown.split('\n');
  const contentLines = lines.slice(1); // Skip title line
  return contentLines.join(' ').substring(0, maxLength);
}

codeExamples.newNoteScreen:
// app/note/new.tsx
import { useState, useRef, useEffect } from 'react';
import { View, KeyboardAvoidingView } from 'react-native';
import { MarkdownTextInput } from 'react-native-live-markdown';
import { MarkdownToolbar } from '@/components/markdown/markdown-toolbar';
import { useRouter } from 'expo-router';
import { notesService } from '@/services/notes';
import { extractTitle } from '@/utils/note-parser';

export default function NewNoteScreen() {
  const [content, setContent] = useState('');
  const [selection, setSelection] = useState({ start: 0, end: 0 });
  const router = useRouter();
  const textInputRef = useRef(null);

  // Auto-save
  useEffect(() => {
    if (!content.trim()) return;
    const timer = setTimeout(async () => {
      const title = extractTitle(content);
      await notesService.createNote(title, content);
    }, 1000);
    return () => clearTimeout(timer);
  }, [content]);

  const handleInsert = (before: string, after: string) => {
    const { start, end } = selection;
    const selectedText = content.substring(start, end);
    if (start !== end) {
      const newContent = content.substring(0, start) + before + selectedText + after + content.substring(end);
      setContent(newContent);
    } else {
      const newContent = content.substring(0, start) + before + after + content.substring(start);
      setContent(newContent);
    }
  };

  return (
    <KeyboardAvoidingView style={{ flex: 1 }}>
      <MarkdownTextInput
        ref={textInputRef}
        value={content}
        onChangeText={setContent}
        onSelectionChange={(e) => setSelection(e.nativeEvent.selection)}
        autoFocus
        multiline
        placeholder="Start typing..."
      />
      <MarkdownToolbar onInsert={handleInsert} />
    </KeyboardAvoidingView>
  );
}

