// components/markdown/markdown-toolbar.tsx
import { View, TouchableOpacity, Text, StyleSheet } from 'react-native';
import { useThemeColors } from '@/hooks/use-theme-colors';

interface MarkdownToolbarProps {
  onInsert: (before: string, after: string) => void;
}

export function MarkdownToolbar({ onInsert }: MarkdownToolbarProps) {
  const { colors } = useThemeColors();

  return (
    <View style={[styles.toolbar, { backgroundColor: colors.surface, borderTopColor: colors.border }]}>
      <ToolbarButton text="B" onPress={() => onInsert('**', '**')} />
      <ToolbarButton text="I" onPress={() => onInsert('*', '*')} />
      <ToolbarButton text="H1" onPress={() => onInsert('# ', '')} />
      <ToolbarButton text="â€¢" onPress={() => onInsert('- ', '')} />
    </View>
  );
}

function ToolbarButton({ text, onPress }) {
  return (
    <TouchableOpacity style={styles.button} onPress={onPress}>
      <Text style={styles.buttonText}>{text}</Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  toolbar: {
    flexDirection: 'row',
    borderTopWidth: 1,
    paddingHorizontal: 8,
    paddingVertical: 8,
    gap: 8,
  },
  button: {
    width: 40,
    height: 40,
    borderRadius: 8,
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonText: {
    fontSize: 16,
    fontWeight: '600',
  },
});
