storageInterface.interface:
export interface StorageProvider {
  // Notes CRUD
  getNotes(): Promise<Note[]>;
  getNote(id: string): Promise<Note>;
  createNote(note: Omit<Note, 'id' | 'created_at' | 'updated_at'>): Promise<Note>;
  updateNote(id: string, note: Partial<Note>): Promise<Note>;
  deleteNote(id: string): Promise<void>;

  // Search
  searchNotes(query: string): Promise<Note[]>;

  // Folders (optional - not all providers may support)
  getFolders?(): Promise<Folder[]>;
  createFolder?(name: string): Promise<Folder>;
  updateFolder?(id: string, name: string): Promise<Folder>;
  deleteFolder?(id: string): Promise<void>;

  // Auth
  signIn(email: string, password: string): Promise<{ user: User; error?: any }>;
  signUp(email: string, password: string): Promise<{ user: User; error?: any }>;
  signOut(): Promise<void>;
  getCurrentUser(): Promise<User | null>;

  // Sync (for local providers with cloud backup)
  sync?(): Promise<void>;
}

