supabase.implementation:
export class SupabaseProvider implements StorageProvider {
  async getNotes(): Promise<Note[]> {
    const { data, error } = await supabase
      .from('notes')
      .select('*')
      .order('created_at', { ascending: false });
    if (error) throw error;
    return data;
  }

  async createNote(note: Omit<Note, 'id' | 'created_at' | 'updated_at'>): Promise<Note> {
    const { data, error } = await supabase
      .from('notes')
      .insert(note)
      .select()
      .single();
    if (error) throw error;
    return data;
  }

  async signIn(email: string, password: string) {
    return await supabase.auth.signInWithPassword({ email, password });
  }

  // ... other methods
}

googleSheets.implementation:
export class SheetsProvider implements StorageProvider {
  private sheets = google.sheets('v4');
  private spreadsheetId: string;

  async getNotes(): Promise<Note[]> {
    const auth = await GoogleSignin.getTokens();
    const response = await this.sheets.spreadsheets.values.get({
      auth: auth.accessToken,
      spreadsheetId: this.spreadsheetId,
      range: 'Notes!A2:H',
    });
    const rows = response.data.values || [];
    return rows.map(row => ({
      id: row[0],
      title: row[1],
      content: row[2],
      user_id: row[3],
      folder_id: row[4],
      is_private: row[5] === 'true',
      created_at: row[6],
      updated_at: row[7],
    }));
  }

  async createNote(note: Omit<Note, 'id' | 'created_at' | 'updated_at'>): Promise<Note> {
    const id = generateId();
    const now = new Date().toISOString();
    await this.sheets.spreadsheets.values.append({
      spreadsheetId: this.spreadsheetId,
      range: 'Notes!A:H',
      valueInputOption: 'RAW',
      requestBody: {
        values: [[id, note.title, note.content, note.user_id, note.folder_id || '', note.is_private ? 'true' : 'false', now, now]]
      }
    });
    return { ...note, id, created_at: now, updated_at: now };
  }

  // ... other methods
}

sqlite.implementation:
export class SQLiteProvider implements StorageProvider {
  private db: SQLite.Database;

  constructor() {
    this.db = SQLite.openDatabase('noted.db');
    this.initDatabase();
  }

  private async initDatabase() {
    await this.db.execAsync(`
      CREATE TABLE IF NOT EXISTS notes (
        id TEXT PRIMARY KEY,
        title TEXT,
        content TEXT,
        user_id TEXT,
        folder_id TEXT,
        is_private INTEGER,
        created_at TEXT,
        updated_at TEXT
      );
      CREATE INDEX IF NOT EXISTS idx_notes_created_at ON notes(created_at);
      CREATE INDEX IF NOT EXISTS idx_notes_user_id ON notes(user_id);
    `);
  }

  async getNotes(): Promise<Note[]> {
    const result = await this.db.getAllAsync('SELECT * FROM notes ORDER BY created_at DESC');
    return result.map(row => ({ ...row, is_private: row.is_private === 1 }));
  }

  async createNote(note: Omit<Note, 'id' | 'created_at' | 'updated_at'>): Promise<Note> {
    const id = generateId();
    const now = new Date().toISOString();
    await this.db.runAsync(
      'INSERT INTO notes (id, title, content, user_id, folder_id, is_private, created_at, updated_at) VALUES (?, ?, ?, ?, ?, ?, ?, ?)',
      [id, note.title, note.content, note.user_id, note.folder_id, note.is_private ? 1 : 0, now, now]
    );
    return { ...note, id, created_at: now, updated_at: now };
  }

  // Optional: Sync to cloud backup
  async sync() {
    // Export to cloud provider (Supabase, Dropbox, etc.)
  }

  // ... other methods
}

firebase.implementation:
export class FirebaseProvider implements StorageProvider {
  async getNotes(): Promise<Note[]> {
    const user = auth().currentUser;
    if (!user) throw new Error('Not authenticated');
    const snapshot = await firestore()
      .collection('notes')
      .where('user_id', '==', user.uid)
      .orderBy('created_at', 'desc')
      .get();
    return snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as Note));
  }

  async createNote(note: Omit<Note, 'id' | 'created_at' | 'updated_at'>): Promise<Note> {
    const now = new Date().toISOString();
    const docRef = await firestore().collection('notes').add({
      ...note,
      created_at: now,
      updated_at: now
    });
    return { ...note, id: docRef.id, created_at: now, updated_at: now };
  }

  async signIn(email: string, password: string) {
    const result = await auth().signInWithEmailAndPassword(email, password);
    return { user: result.user };
  }

  // ... other methods
}

providerFactory.implementation:
export class StorageFactory {
  static create(type: StorageType, config?: any): StorageProvider {
    switch (type) {
      case 'supabase':
        return new SupabaseProvider(config);
      case 'sheets':
        return new SheetsProvider(config);
      case 'sqlite':
        return new SQLiteProvider();
      case 'firebase':
        return new FirebaseProvider(config);
      case 'notion':
        return new NotionProvider(config);
      case 'airtable':
        return new AirtableProvider(config);
      case 'dropbox':
        return new DropboxProvider(config);
      case 'custom':
        return new CustomAPIProvider(config);
      default:
        throw new Error(`Unknown storage type: ${type}`);
    }
  }

  static getSupportedTypes(): StorageType[] {
    return ['supabase', 'sheets', 'sqlite', 'firebase'];
  }

  static getProviderInfo(type: StorageType) {
    const info = {
      supabase: { name: 'Supabase', icon: 'üóÑÔ∏è', description: 'Cloud database with real-time sync', requiresInternet: true },
      sheets: { name: 'Google Sheets', icon: 'üìä', description: 'Store notes in a spreadsheet', requiresInternet: true },
      sqlite: { name: 'Local Only', icon: 'üì±', description: 'Private, offline storage', requiresInternet: false },
      firebase: { name: 'Firebase', icon: 'üî•', description: 'Google Cloud database', requiresInternet: true },
    };
    return info[type];
  }
}

storageContext.implementation:
interface StorageContextType {
  provider: StorageProvider;
  storageType: StorageType;
  switchProvider: (type: StorageType, config?: any) => Promise<void>;
  isLoading: boolean;
}

const StorageContext = createContext<StorageContextType>(null!);

export function StorageProvider({ children }) {
  const [storageType, setStorageType] = useState<StorageType>('supabase');
  const [provider, setProvider] = useState<StorageProvider>(StorageFactory.create('supabase'));
  const [isLoading, setIsLoading] = useState(true);

  // Load saved preference on mount
  useEffect(() => {
    AsyncStorage.getItem('storage_type').then(type => {
      if (type) {
        setStorageType(type as StorageType);
        setProvider(StorageFactory.create(type as StorageType));
      }
      setIsLoading(false);
    });
  }, []);

  const switchProvider = async (type: StorageType, config?: any) => {
    setIsLoading(true);
    const newProvider = StorageFactory.create(type, config);
    setProvider(newProvider);
    setStorageType(type);
    await AsyncStorage.setItem('storage_type', type);
    if (config) {
      await AsyncStorage.setItem(`storage_config_${type}`, JSON.stringify(config));
    }
    setIsLoading(false);
  };

  return (
    <StorageContext.Provider value={{ provider, storageType, switchProvider, isLoading }}>
      {children}
    </StorageContext.Provider>
  );
}

export const useStorage = () => useContext(StorageContext);

