import { useStorage } from '@/contexts/storage-context';

export function useNotes() {
  const { provider } = useStorage();

  const getNotes = async () => {
    return await provider.getNotes();
  };

  const createNote = async (title: string, content: string, folderId?: string, isPrivate = false) => {
    const { data: { user } } = await provider.getCurrentUser();
    return await provider.createNote({
      title,
      content,
      user_id: user.id,
      folder_id: folderId,
      is_private: isPrivate
    });
  };

  const updateNote = async (id: string, title: string, content: string) => {
    return await provider.updateNote(id, { title, content });
  };

  const deleteNote = async (id: string) => {
    return await provider.deleteNote(id);
  };

  return { getNotes, createNote, updateNote, deleteNote };
}

