exportImport.export:
async exportNotes(): Promise<Note[]> {
  const { provider } = useStorage();
  return await provider.getNotes();
}

exportImport.import:
async importNotes(notes: Note[], targetProvider: StorageProvider): Promise<void> {
  for (const note of notes) {
    await targetProvider.createNote({
      title: note.title,
      content: note.content,
      user_id: note.user_id,
      folder_id: note.folder_id,
      is_private: note.is_private
    });
  }
}

exportImport.migration:
async migrateToProvider(fromType: StorageType, toType: StorageType): Promise<void> {
  const fromProvider = StorageFactory.create(fromType);
  const toProvider = StorageFactory.create(toType);

  // Export from source
  const notes = await fromProvider.getNotes();

  // Import to destination
  await importNotes(notes, toProvider);

  // Switch active provider
  await switchProvider(toType);
}

