// Add to image-dialog-modal.tsx

// 1. New imports (add to top of file)
import React, { useState, useRef, useEffect } from 'react';
import { Platform } from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import { imagesService } from '@/services/images';
import { supabase } from '@/lib/supabase';
import { toast } from 'sonner-native';

// 2. New state (add with other useState calls)
const [uploading, setUploading] = useState(false);

// 2b. Add mountedRef to prevent race condition
const mountedRef = useRef(true);

useEffect(() => {
  return () => {
    mountedRef.current = false;
  };
}, []);

// 3. New handler function (add before handleInsert)
const handleUploadImage = async () => {
  try {
    // Request permissions (skip on Web, automatically handled on modern native OS)
    if (Platform.OS !== 'web') {
      const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();

      if (!permissionResult.granted) {
        toast.error('Permission to access photos is required');
        return;
      }
    }

    // Launch image picker
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      quality: 0.8, // Compress to reduce file size
      aspect: [16, 9],
    });

    if (result.canceled) return;

    // Get user ID
    const { data: { user } } = await supabase.auth.getUser();
    if (!user) {
      toast.error('You must be logged in to upload images');
      return;
    }

    // Upload image
    if (mountedRef.current) setUploading(true);
    const uploadResult = await imagesService.uploadImage(result.assets[0].uri, user.id);

    // Auto-fill URL field (only if still mounted)
    if (mountedRef.current) {
      setImageUrl(uploadResult.publicUrl);
      toast.success('Image uploaded successfully');
    }
  } catch (error: any) {
    console.error('Upload error:', error);
    if (mountedRef.current) {
      toast.error(error.message || 'Failed to upload image');
    }
  } finally {
    if (mountedRef.current) setUploading(false);
  }
};

// 4. Upload button JSX (add between header and Alt Text input, around line 74)
{/* Upload Button */}
<TouchableOpacity
  style={[
    styles.uploadButton,
    {
      backgroundColor: colors.background,
      borderColor: colors.border,
    },
    uploading && styles.buttonDisabled,
  ]}
  onPress={handleUploadImage}
  activeOpacity={0.7}
  disabled={uploading}
>
  <MaterialIcons
    name="photo-library"
    size={20}
    color={uploading ? colors.textSecondary : colors.tint}
  />
  <Text
    style={[
      styles.uploadButtonText,
      { color: uploading ? colors.textSecondary : colors.text },
    ]}
  >
    {uploading ? 'Uploading...' : 'Upload from Device'}
  </Text>
</TouchableOpacity>

<Text style={[styles.dividerText, { color: colors.textSecondary }]}>or</Text>

// 5. New styles (add to StyleSheet.create)
uploadButton: {
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  gap: 8,
  padding: 14,
  borderRadius: 8,
  borderWidth: 1,
  marginBottom: 12,
},
dividerText: {
  textAlign: 'center',
  fontSize: 14,
  fontWeight: '500',
  marginBottom: 12,
},
uploadButtonText: {
  fontSize: 16,
  fontWeight: '600',
},

