codeExamples.googleDriveHelper:
// Optional: Helper to transform Google Drive URLs
// Add to utils/image-helpers.ts if Google Drive URLs still don't render

export function transformGoogleDriveUrl(url: string): string {
  // Check if it's a Google Drive sharing link
  const driveMatch = url.match(/drive\.google\.com\/file\/d\/([a-zA-Z0-9_-]+)/);

  if (driveMatch) {
    const fileId = driveMatch[1];
    // Transform to direct image URL
    return `https://drive.google.com/uc?export=view&id=${fileId}`;
  }

  return url; // Return original URL if not Google Drive
}

// Then in image-dialog-modal.tsx handleInsert:
const transformedUrl = transformGoogleDriveUrl(imageUrl.trim());
onInsert(altText.trim(), transformedUrl);

