// Add to app.json (or app.config.js)
// Required for iOS photo library access and Android media permissions

{
  "expo": {
    "plugins": [
      [
        "expo-image-picker",
        {
          "photosPermission": "The app needs access to your photos to upload images to your notes.",
          "cameraPermission": "The app needs access to your camera to take photos for your notes."
        }
      ]
    ],
    "ios": {
      "infoPlist": {
        "NSPhotoLibraryUsageDescription": "This app needs access to your photo library to upload images to your notes.",
        "NSCameraUsageDescription": "This app needs access to your camera to take photos for your notes."
      }
    },
    "android": {
      "permissions": [
        "READ_MEDIA_IMAGES",
        "CAMERA"
      ]
    }
  }
}

// Note: READ_MEDIA_IMAGES is for Android 13+ (API 33+).
// For backward compatibility with Android 12 and below, expo-image-picker
// will automatically request READ_EXTERNAL_STORAGE when needed.
// WRITE_EXTERNAL_STORAGE is no longer required for uploads.

// After adding permissions, rebuild the native app:
// iOS: npx expo run:ios
// Android: npx expo run:android
// Or create new development build: eas build --profile development

