// services/images.ts
import { supabase } from '@/lib/supabase';

export const imagesService = {
  /**
   * Upload image to Supabase Storage
   * @param imageUri - Local file URI from image picker
   * @param userId - Current user's ID
   * @returns Public URL of uploaded image
   */
  async uploadImage(imageUri: string, userId: string): Promise<string> {
    try {
      // Generate unique filename
      const timestamp = Date.now();
      const randomString = Math.random().toString(36).substring(2, 9);
      const fileExt = imageUri.split('.').pop()?.toLowerCase() || 'jpg';
      const fileName = `${timestamp}_${randomString}.${fileExt}`;
      const filePath = `${userId}/${fileName}`;

      // Convert URI to blob for upload
      const response = await fetch(imageUri);
      const blob = await response.blob();

      // Check file size (5MB limit)
      if (blob.size > 5242880) {
        throw new Error('Image must be less than 5MB');
      }

      // Upload to Supabase Storage
      const { data, error } = await supabase.storage
        .from('note-images')
        .upload(filePath, blob, {
          contentType: `image/${fileExt}`,
          cacheControl: '3600',
          upsert: false
        });

      if (error) throw error;

      // Get public URL
      const { data: { publicUrl } } = supabase.storage
        .from('note-images')
        .getPublicUrl(filePath);

      return publicUrl;
    } catch (error) {
      console.error('Image upload failed:', error);
      throw error;
    }
  },

  /**
   * Delete image from Supabase Storage
   * @param imageUrl - Full public URL of image
   */
  async deleteImage(imageUrl: string): Promise<void> {
    try {
      // Extract file path from URL
      // URL format: https://[project].supabase.co/storage/v1/object/public/note-images/[userId]/[filename]
      const urlParts = imageUrl.split('/note-images/');
      if (urlParts.length !== 2) {
        throw new Error('Invalid image URL format');
      }
      const filePath = urlParts[1];

      const { error } = await supabase.storage
        .from('note-images')
        .remove([filePath]);

      if (error) throw error;
    } catch (error) {
      console.error('Image deletion failed:', error);
      throw error;
    }
  }
};

