option2_js_computation.code:
async getFoldersWithNoteCounts(): Promise<FolderWithCount[]> {
  // Fetch all folders
  const folders = await this.getFolders();

  // Fetch all notes
  const { data: notes, error } = await supabase
    .from('notes')
    .select('id, folder_id');

  if (error) throw error;

  // Count notes per folder
  const countMap = new Map<string, number>();
  notes?.forEach(note => {
    if (note.folder_id) {
      countMap.set(note.folder_id, (countMap.get(note.folder_id) || 0) + 1);
    }
  });

  // Add counts to folders
  return folders.map(folder => ({
    ...folder,
    note_count: countMap.get(folder.id) || 0
  }));
}

getTotalNoteCount.code:
async getTotalNoteCount(): Promise<number> {
  const { count, error } = await supabase
    .from('notes')
    .select('*', { count: 'exact', head: true });

  if (error) throw error;
  return count || 0;
}

getUnfiledNoteCount.code:
async getUnfiledNoteCount(): Promise<number> {
  const { count, error } = await supabase
    .from('notes')
    .select('*', { count: 'exact', head: true })
    .is('folder_id', null);

  if (error) throw error;
  return count || 0;
}

