// components/export/export-theme-modal.tsx
import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Modal, StyleSheet, ScrollView } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import { useThemeColors } from '@/hooks/use-theme-colors';
import { Themes, ThemeName } from '@/constants/theme';

interface ExportThemeModalProps {
  visible: boolean;
  currentTheme: ThemeName;
  currentMode: 'light' | 'dark';
  onConfirm: (theme: ThemeName, mode: 'light' | 'dark') => void;
  onCancel: () => void;
}

export function ExportThemeModal({
  visible,
  currentTheme,
  currentMode,
  onConfirm,
  onCancel
}: ExportThemeModalProps) {
  const { colors } = useThemeColors();
  const [selectedTheme, setSelectedTheme] = useState<ThemeName>(currentTheme);
  const [selectedMode, setSelectedMode] = useState<'light' | 'dark'>(currentMode);

  const handleUseCurrent = () => {
    onConfirm(currentTheme, currentMode);
  };

  const handleConfirm = () => {
    onConfirm(selectedTheme, selectedMode);
  };

  return (
    <Modal visible={visible} transparent animationType="fade" onRequestClose={onCancel}>
      <View style={styles.overlay}>
        <View style={[styles.modal, { backgroundColor: colors.surface, borderColor: colors.border }]}>
          {/* Header */}
          <View style={styles.header}>
            <MaterialIcons name="palette" size={24} color={colors.tint} />
            <Text style={[styles.title, { color: colors.text }]}>Export Theme</Text>
          </View>

          {/* Quick Option: Use Current */}
          <TouchableOpacity
            style={[styles.option, { backgroundColor: colors.background, borderColor: colors.border }]}
            onPress={handleUseCurrent}
            activeOpacity={0.7}
          >
            <View style={styles.optionContent}>
              <Text style={[styles.optionTitle, { color: colors.text }]}>Use Current Theme</Text>
              <Text style={[styles.optionDesc, { color: colors.textSecondary }]}>
                Export with {Themes[currentTheme].displayName} ({currentMode} mode)
              </Text>
            </View>
            <MaterialIcons name="chevron-right" size={24} color={colors.textSecondary} />
          </TouchableOpacity>

          <Text style={[styles.divider, { color: colors.textSecondary }]}>or</Text>

          {/* Mode Toggle */}
          <View style={styles.modeToggle}>
            <TouchableOpacity
              style={[
                styles.modeButton,
                selectedMode === 'light' && { backgroundColor: colors.tint },
                { borderColor: colors.border }
              ]}
              onPress={() => setSelectedMode('light')}
            >
              <Text style={{ color: selectedMode === 'light' ? '#fff' : colors.text }}>Light</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={[
                styles.modeButton,
                selectedMode === 'dark' && { backgroundColor: colors.tint },
                { borderColor: colors.border }
              ]}
              onPress={() => setSelectedMode('dark')}
            >
              <Text style={{ color: selectedMode === 'dark' ? '#fff' : colors.text }}>Dark</Text>
            </TouchableOpacity>
          </View>

          {/* Theme Grid */}
          <ScrollView style={styles.themeGrid} showsVerticalScrollIndicator={false}>
            <View style={styles.grid}>
              {(Object.keys(Themes) as ThemeName[]).map((themeName) => {
                const theme = Themes[themeName];
                const palette = theme[selectedMode];
                const isSelected = selectedTheme === themeName;

                return (
                  <TouchableOpacity
                    key={themeName}
                    style={[
                      styles.themeCard,
                      { borderColor: isSelected ? colors.tint : colors.border },
                      isSelected && { borderWidth: 2 }
                    ]}
                    onPress={() => setSelectedTheme(themeName)}
                    activeOpacity={0.7}
                  >
                    {/* Theme preview */}
                    <View style={styles.colorPreview}>
                      <View style={{ backgroundColor: palette.background, flex: 1 }} />
                      <View style={{ backgroundColor: palette.surface, flex: 1 }} />
                      <View style={{ backgroundColor: palette.tint, flex: 1 }} />
                    </View>
                    <Text style={[styles.themeName, { color: colors.text }]}>
                      {theme.displayName}
                    </Text>
                    {isSelected && (
                      <MaterialIcons name="check-circle" size={20} color={colors.tint} style={styles.checkIcon} />
                    )}
                  </TouchableOpacity>
                );
              })}
            </View>
          </ScrollView>

          {/* Buttons */}
          <View style={styles.buttons}>
            <TouchableOpacity
              style={[styles.button, { backgroundColor: colors.background }]}
              onPress={onCancel}
              activeOpacity={0.7}
            >
              <Text style={[styles.buttonText, { color: colors.textSecondary }]}>Cancel</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={[styles.button, { backgroundColor: colors.tint }]}
              onPress={handleConfirm}
              activeOpacity={0.7}
            >
              <Text style={[styles.buttonText, { color: '#fff' }]}>Export</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </Modal>
  );
}

