// services/markdown-service.ts (UPDATED)
import { marked } from 'marked';
import { ThemeName, Themes } from '@/constants/theme';
import { generateThemeCSS, generateInteractiveJS } from '@/utils/export-theme-css';

export const markdownService = {
  renderToHtml(markdown: string): string {
    return marked(markdown) as string;
  },

  /**
   * Convert markdown to full HTML document with theme-aware styling
   * @param title - Document title
   * @param markdown - Raw markdown text
   * @param theme - Theme name (optional, defaults to 'monochrome')
   * @param mode - Color mode (optional, defaults to 'light')
   * @returns Complete HTML document with interactive features
   */
  renderToDocument(
    title: string,
    markdown: string,
    theme: ThemeName = 'monochrome',
    mode: 'light' | 'dark' = 'light'
  ): string {
    const htmlContent = marked(markdown) as string;
    const themeCSS = generateThemeCSS(theme, mode);
    const interactiveJS = generateInteractiveJS(mode);
    const themeName = Themes[theme].displayName;

    return `<!DOCTYPE html>
<html lang="en" data-theme="${mode}">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<meta name="theme-name" content="${themeName}">
<meta name="color-scheme" content="light dark">
<title>${title}</title>
<style>
${themeCSS}
</style>
</head>
<body>
<!-- Theme Toggle Button -->
<button class="theme-toggle" onclick="toggleTheme()">
  ${mode === 'light' ? 'üåô Dark' : '‚òÄÔ∏è Light'}
</button>

<!-- Note Content -->
${htmlContent}

<!-- Interactive JavaScript -->
${interactiveJS}
</body>
</html>`;
  }
};

