// app/note-editor/[id].tsx - Updated handleExport()
// Add state for theme modal
const [showThemeModal, setShowThemeModal] = useState(false);

// Updated handleExport - shows theme modal first
const handleExport = () => {
  setShowThemeModal(true);
};

// New function - called after theme selection
const handleExportWithTheme = async (theme: ThemeName, mode: 'light' | 'dark') => {
  try {
    const title = extractTitle(content) || 'Note';
    const htmlContent = markdownService.renderToDocument(title, content, theme, mode);

    if (Platform.OS === 'web') {
      // Web: Download as file
      const blob = new Blob([htmlContent], { type: 'text/html' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${title}.html`;
      link.click();
      URL.revokeObjectURL(url);
      toast.success('HTML exported successfully');
    } else {
      // Mobile: Share sheet
      await Share.share({
        message: htmlContent,
        title: `${title}.html`,
      });
    }
  } catch (error) {
    console.error('Export failed:', error);
    toast.error('Failed to export HTML');
  } finally {
    setShowThemeModal(false);
  }
};

// In JSX - add modal
<ExportThemeModal
  visible={showThemeModal}
  currentTheme={themeName} // from useThemeColors hook
  currentMode={colorScheme === 'dark' ? 'dark' : 'light'}
  onConfirm={handleExportWithTheme}
  onCancel={() => setShowThemeModal(false)}
/>
